INSERT INTO `recipes` (`id_recette`, `recipe_name`, `ingredients`, `prep_time`, `servings`, `cook_time`, `recipe_description`, `recipe_img`, `recipe_category`) VALUES
(1, 'Thé au Safran de la Ventue', '<ul>\r\n<li itemprop=\"ingredients\">1 cuillère à soupe de thé vert</li>\r\n<li itemprop=\"ingredients\">8 pistils de Safran de la Ventue</li>\r\n<li itemprop=\"ingredients\">1L d\'eau</li>\r\n</ul>', 5, '8 à 10', 'Ne pas faire bouillir l\'eau', 'Verser 1 cuillère à soupe de thé vert dans une théière,<br />Ajouter 8 pistils de Safran.<br />Ajouter sucre ou miel à votre convenance.<br />Ajouter un peu d\'eau chaude et laisser infuser. Pendant ce temps, chauffer 1L d\'eau portée à ébullition puis versez-le.<br />Couvrir et laisser infuser 5 à 10min suivant vos goûts.', 'images/safran_seul.png', 1),
(2, 'Sauce chaude au Safran de la Ventue', '<ul>\r\n<li itemprop=\"ingredients\">4 cuillères à soupe de Lait</li>\r\n<li itemprop=\"ingredients\">20 à 40cl de crème liquide</li>\r\n<li itemprop=\"ingredients\">2 pistils de Safran par personne</li>\r\n</ul>', 15, '-', 'Pas de cuisson', 'La veille, infuser le Safran de la Ventue dans le lait chaud.<br />Le lendemain, chauffer la crème liquide puis verser le lait safrané, votre crème prendra une teinte jaune-orangée.<br />Salez et poivrez à votre convenance.<br /> Utiliser sur viandes blanches, poissons, volailles...', 'images/safran_seul.png', 2),
(3, 'Clafoutis au Safran de la Ventue', '<ul>\r\n<li itemprop=\"ingredients\">20cl de lait</li>\r\n<li itemprop=\"ingredients\">3 oeufs</li>\r\n<li itemprop=\"ingredients\">100g de sucre</li>\r\n<li itemprop=\"ingredients\">120g de farine</li>\r\n<li itemprop=\"ingredients\">30g de beurre (facultatif)</li>\r\n<li>500g de cerises, pommes-poires, ananas...</li>\r\n<li itemprop=\"ingredients\">8 pistils de Safran de la Ventue</li>\r\n</ul>', 20, '5 à 6', '30min - thermostat 7(240°c)', 'Laisser infuser les pistils de Safran de la Ventue dans de l\'eau tiède.<br />Battre les oeufs entiers avec le sucre. Ajouter la farine. Bien mélanger. Ajouter le beurre fondu (facultatif)<br />Verser peu à peu le lait froid<br />Beurrer le plat, disposer les fruits. Recouvrir de la préparation.<br />Faire cuire et vérifier la cuisson à l\'aide de la pointe d\'un couteau.', 'images/safran_seul.png', 1),
(4, 'Moules au Safran de la Ventue', '<ul>\r\n<li itemprop=\"ingredients\">1 kilo de moules</li>\r\n<li itemprop=\"ingredients\">20cl de crème</li>\r\n<li itemprop=\"ingredients\">6 pistils au Safran de la ventue</li>\r\n</ul>', 10, '1', 'Quelques minutes', 'Infuser le Safran dans la crème tiédie,<br />Nettoyer les moules,<br />Verser les moules dans un fait-tout, lorsqu’elles commence à s\'ouvrir, retirer l\'eau et ajouter la crème safranée.<br />Finir de faire cuire quelques minutes, dès qu\'elles sont ouvertes, vos moules sont prêtes à être dégustées.', 'images/safran_seul.png', 2),
(5, 'Omelette au Safran de la Ventue', '<ul>\r\n<li itemprop=\"ingredients\">8 oeufs (selon votre appétit)</li>\r\n<li itemprop=\"ingredients\">8 pistils de Safran de la Ventue</li>\r\n<li itemprop=\"ingredients\">3 Cuillères à soupe de lait</li>\r\n<li itemprop=\"ingredients\">Beurre si nécessaire</li>\r\n<li itemprop=\"ingredients\">Sel et poivre à votre convenance</li>\r\n</ul>', 5, '4', '3 min', 'Infuser les pistils de Safran de la Ventue dans le lait tiède,<br />Battre les oeufs, ajouter le lait safrané, saler et poivrer à votre convenance.<br />Beurrer votre poêle et faire cuire, votre omelette est prête !', 'images/safran_seul.png', 2),
(6, 'Crème anglaise au Safran de la Ventue', '<ul>\r\n<li itemprop=\"ingredients\">50cl de lait</li>\r\n<li itemprop=\"ingredients\">4 jaunes d\'oeuf</li>\r\n<li itemprop=\"ingredients\">75g de sucre</li>\r\n<li itemprop=\"ingredients\">12 pistils de Safran de la Ventue</li>\r\n</ul>', 10, '4 à 6', 'A feux doux', 'La veille, infuser le Safran dans le lait.<br />Fouetter les jaunes d’œuf et le sucre,<br />Chauffer le lait et le verser dans le mélange oeuf-sucre sans cesser de remuer.<br />Epaissir à feu doux sans laisser bouillir.<br />La crème est prête lorsqu\'elle nappe la cuillère.<br />Mettre au réfrigérateur. A déguster bien froid.', 'images/safran_seul.png', 1);
